import React,{Component} from 'react';

export class TestTask extends Component{

    constructor(props){
        super(props);
        this.state={
            tools:[],
            users:[],
            modalTitle:"",
            name:"",
            id:0,
            count:0,
            sid:0,
            summary:[]
        }
    }

    refreshList(){
        fetch('https://localhost:44385/api/Table')
        .then(response=>response.json())
        .then(data=>{
            this.setState({summary:data});
        });
    }

    componentDidMount(){
        this.refreshList();
        this.GetUsers();
        this.GetTools();
    }
    
    addClick(){
        this.setState({
            modalTitle:"Добавление записи",
        });
    }

    GetUsers(){
        fetch('https://localhost:44385/api/Users')
        .then(response=>response.json())
        .then(data=>{
            this.setState({users:data});
            console.log(data)
        });
    }

    GetTools(){
        fetch('https://localhost:44385/api/Tools')
        .then(response=>response.json())
        .then(data=>{
            this.setState({tools:data});
            console.log(data)
        });
    }

    editClick(emp){
        this.setState({
            modalTitle:"Edit Employee",
            EmployeeId:emp.EmployeeId,
            EmployeeName:emp.EmployeeName,
            Department:emp.Department,
            DateOfJoining:emp.DateOfJoining,
            PhotoFileName:emp.PhotoFileName
        });
    }

    deleteClick(id){
        if(window.confirm('Are you sure?')){
        fetch('https://localhost:44385/api/Summary/'+ id,{
            method:'DELETE',
            headers:{
                'Accept':'application/json',
                'Content-Type':'application/json'
            }
        })
        .then(res=>res.json())
        .then((result)=>{
            this.refreshList();
        },(error)=>{
            alert('100');
        })
        console.log(id)
        }
    }

    setOptionUsers(data) {
        const s = data.map(user => (
            {value:user.id,label:user.FIO}
            
        ))
        
        return s;
    }

    setOptionTools(data) {
        const s = data.map(tool => (
            {value:tool.id,label:tool.name}
        ))
        return s;
    }
    setOptionCount(data) {
        const s = data.map(tool => (
            {value:tool.id,label:tool.countTols}
        ))
        return s;
    }
    pushData(){
        fetch('https://localhost:44385/api/Summary',{
            method:'POST',
            headers:{
                'Accept':'application/json',
                'Content-Type':'application/json'
            },
            body:JSON.stringify({
                id_tools:this.state.id_tools,
                id_users:this.state.id_users,
                countTols:this.state.countTols
            })
            
        })
        .then(res=>res.json())
        .then((result)=>{
            alert(result);
            this.refreshList();
        },(error)=>{
            alert('Проверьте корректность введённых данных');
            this.refreshList();
        })
}

    render(){
        const {
            tools,
            modalTitle,
            Toolsid,
            name,
            count,
            summary
        }=this.state;

        return(
<div>

    <button type="button"
    className="btn btn-primary m-2 float-end"
    data-bs-toggle="modal"
    data-bs-target="#exampleModal"
    onClick={()=>this.addClick()}>
        Добавить запись
    </button>
    <table className="table table-striped">
    <thead>
    <tr>
        <th>
            ФИО Сотрудника
        </th>
        <th>
            Наименование инструмента
        </th>
        <th>
            Количество
        </th>
        <th>
            Действие
        </th>
    </tr>
    </thead>
    <tbody>
    {summary.map(summary =>
            <tr key={summary.sid}>
                <td>{summary.FIO}</td>
                <td>{summary.name}</td>
                <td>{summary.countTols}</td>
                <td>
                <button type="button"
                className="btn btn-light mr-1"
                onClick={()=>this.deleteClick(emp.EmployeeId)}>
                    <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-trash-fill" viewBox="0 0 16 16">
                    <path d="M2.5 1a1 1 0 0 0-1 1v1a1 1 0 0 0 1 1H3v9a2 2 0 0 0 2 2h6a2 2 0 0 0 2-2V4h.5a1 1 0 0 0 1-1V2a1 1 0 0 0-1-1H10a1 1 0 0 0-1-1H7a1 1 0 0 0-1 1H2.5zm3 4a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7a.5.5 0 0 1 .5-.5zM8 5a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7A.5.5 0 0 1 8 5zm3 .5v7a.5.5 0 0 1-1 0v-7a.5.5 0 0 1 1 0z"/>
                    </svg>
                </button>

                </td>
            </tr>
            )}
    </tbody>
    </table>

<div className="modal fade" id="exampleModal" tabIndex="-1" aria-hidden="true">
<div className="modal-dialog modal-lg modal-dialog-centered">
<div className="modal-content">
   <div className="modal-header">
       <h5 className="modal-title">{modalTitle}</h5>
       <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"
       ></button>
   </div>

   <div className="modal-body">
    <div className="d-flex flex-row bd-highlight mb-3">
     
     <div className="p-2 w-50 bd-highlight">
    
        <div className="input-group mb-3">
            <span className="input-group-text">Emp Name</span>
            <input type="text" className="form-control"
            value={EmployeeName}
            onChange={this.changeEmployeeName}/>
        </div>

        <div className="input-group mb-3">
            <span className="input-group-text">Department</span>
            <select className="form-select"
            onChange={this.changeDepartment}
            value={Department}>
                {departments.map(dep=><option key={dep.DepartmentId}>
                    {dep.DepartmentName}
                </option>)}
            </select>
        </div>

        <div className="input-group mb-3">
            <span className="input-group-text">DOJ</span>
            <input type="date" className="form-control"
            value={DateOfJoining}
            onChange={this.changeDateOfJoining}/>
        </div>


     </div>
     <div className="p-2 w-50 bd-highlight">
         <img width="250px" height="250px"
         src={PhotoPath+PhotoFileName}/>
         <input className="m-2" type="file" onChange={this.imageUpload}/>
     </div>
    </div>

    {EmployeeId==0?
        <button type="button"
        className="btn btn-primary float-start"
        onClick={()=>this.createClick()}
        >Create</button>
        :null}

        {EmployeeId!=0?
        <button type="button"
        className="btn btn-primary float-start"
        onClick={()=>this.updateClick()}
        >Update</button>
        :null}
   </div>

</div>
</div> 
</div>


</div>
        )
    }
}
