import React,{Component} from 'react';
import {variables} from './Variables.js';


import AsyncSelect from 'react-select/async'
import ListTools from './ListTools.js';

export class Tools extends Component{


    constructor(props){
        super(props);

        this.state={
            tools:[],
            modalTitle:"",
            name:"",
            id:0,
            count:0,
        }
    }



    refreshList(){
        fetch(variables.API_URL+'Tools')
        .then(response=>response.json())
        .then(data=>{
            this.setState({tools:data});
        });
    }

   /* handleChange = value =>{
        setValue(value);
    };

    handleChange = value =>{
        setSelectedValue(value);
    }*/


    fetchData=()=>{
        return ListTools.get('/Tools').then(result =>{
            const res = result.data.data;
            return res;
        })
    }

    componentDidMount(){
        this.refreshList();
    }

    changename =(e)=>{
        this.setState({name:e.target.value});
    }

    addClick(){
        this.setState({
            modalTitle:"Add Tools",
            DepartmentId:0,
            name:"",
            count:0
        });
    }
    editClick(tools){
        this.setState({
            modalTitle:"Edit Tools",
            id:tools.DepartmentId,
            name:tools.name,
            count:tools.count
        });
    }

    createClick(){
        fetch(variables.API_URL+'Tools',{
            method:'POST',
            headers:{
                'Accept':'application/json',
                'Content-Type':'application/json'
            },
            body:JSON.stringify({
                id:this.state.id,
                name:this.state.name,
                count:this.state.count
            })
        })
        .then(res=>res.json())
        .then((result)=>{
            alert(result);
            this.refreshList();
        },(error)=>{
            alert('Failed');
        })
    }


    deleteClick(id){
        if(window.confirm('Are you sure?')){
        fetch(variables.API_URL+'department/'+id,{
            method:'DELETE',
            headers:{
                'Accept':'application/json',
                'Content-Type':'application/json'
            }
        })
        .then(res=>res.json())
        .then((result)=>{
            alert(result);
            this.refreshList();
        },(error)=>{
            alert('Failed');
        })
        }
    }

    render(){
        const {
            tools,
            modalTitle,
            Toolsid,
            name,
            count
        }=this.state;

        return(
<div>
<button type="button"
    className="btn btn-primary m-2 float-end"
    data-bs-toggle="modal"
    data-bs-target="#exampleModal"
    onClick={()=>this.addClick()}>
        Добавить запись
    </button> 
    <table className="table table-striped">
    <thead>
    <tr>
        <th>
            FIO      
        </th>
        <th>
            ToolsName
        </th>
        <th>
            Count
        </th>
        <th>
            Options
        </th>
    </tr>
    </thead>
    <tbody>
        {tools.map(tools=>
            <tr key={tools.id}>
                <td>{tools.FIO}</td>
                <td>{tools.name}</td>
                <td>{tools.countTools}</td>
                <td>
                <button type="button"
                className="btn btn-light mr-1"
                onClick={()=>this.deleteClick(tools.id)}>
                    <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-trash-fill" viewBox="0 0 16 16">
                    <path d="M2.5 1a1 1 0 0 0-1 1v1a1 1 0 0 0 1 1H3v9a2 2 0 0 0 2 2h6a2 2 0 0 0 2-2V4h.5a1 1 0 0 0 1-1V2a1 1 0 0 0-1-1H10a1 1 0 0 0-1-1H7a1 1 0 0 0-1 1H2.5zm3 4a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7a.5.5 0 0 1 .5-.5zM8 5a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7A.5.5 0 0 1 8 5zm3 .5v7a.5.5 0 0 1-1 0v-7a.5.5 0 0 1 1 0z"/>
                    </svg>
                </button>

                </td>
            </tr>
            )}
    </tbody>
    </table>

<div className="modal fade" id="exampleModal" tabIndex="-1" aria-hidden="true">
<div className="modal-dialog modal-lg modal-dialog-centered">
<div className="modal-content">
   <div className="modal-header">
       <h5 className="modal-title">{modalTitle}</h5>
       <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"
       ></button>
   </div>

   <div className="modal-body">
       <div className='container'>
            <div className='row alert alert-info'> Выберете инструмент: {JSON.stringify(selectedValue || {},null,2)}</div>
            <div className='row'>
                <div className='col-md-4'></div>
                <div className='col-md-4'>
                    <AsyncSelect
                    cacheOptions
                    defaultOptions
                    value={selectedValue}
                    getOptionLabel = {e.name}
                    getOptionValue = {e => e.id}
  //                  loadOptions {this.fetchData}
                    onInputChange = {handleInputChange}
    //                onChange {(e) => {
     //                   this.handleChange
     //               }}
                /> 
                </div>
                <div className='col-md-4'></div>
            </div>

       </div>
       <div className="input-group mb-3">
        <span className="input-group-text">name</span>
        <input type="text" className="form-control"
        value={name}
        onChange={this.changename}/>
       </div>
       <div className="input-group mb-3">
        <span className="input-group-text">count</span>
        <input type="text" className="form-control"
        value={name}
        onChange={this.changename}/>
       </div>

        {Toolsid==0?
        <button type="button"
        className="btn btn-primary float-start"
        onClick={()=>this.createClick()}
        >Create</button>
        :null}

        {Toolsid!=0?
        <button type="button"
        className="btn btn-primary float-start"
        onClick={()=>this.updateClick()}
        >Update</button>
        :null}

   </div>

</div>
</div> 
</div>


</div>
        )
    }
}
