import React from 'react'
import Select from 'react-select';
import '../../App.css';
export default class PopUp extends React.Component{


    constructor(props){
        super(props);
    

        this.state={
            users:this.props.users,
            userValue:0,
            tools:this.props.tools,
            toolValue:0,
            options:[],
            count:0,
        }

        
    }
    setOptionUsers(data) {
        const s = data.map(user => (
            {value:user.id,label:user.FIO}
        ))
        return s;
    }
    setOptionTools(data) {
        const s = data.map(tool => (
            {value:tool.id,label:tool.name}
        ))
        return s;
    }

    pushData() {
        console.log('helloworld!');
    }



    
    render(){
    
        return(

             
            <div className="modal fade" id="exampleModal" tabIndex="-1" aria-hidden="true">
            <div className="modal-dialog modal-lg modal-dialog-centered">
            <div className="modal-content">
   <div className="modal-header">
       <h5 className="modal-title">Заголовок</h5>
       <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"
       ></button>
   </div>

   <div className="modal-body">
       <div className='container'>
       <Select className="ttt" style="margin-bottom:15px;"onChange={(e)=>{this.setState({userValue:e.value}); console.log(this.state.userValue)}} options={this.setOptionUsers(this.props.users)} />
       <Select className="ttt" onChange={(e)=>{this.setState({toolValue:e.value}); console.log(this.state.toolValue)}} options={this.setOptionTools(this.props.tools)} />
       <input onChange={(e)=>{this.setState({count:e.target.value}); console.log(this.state.count)}} value={this.state.count}/>

       <button onClick={this.pushData}>Update</button>         
                <div className='col-md-4'></div>
            </div>
       </div>


       <div className="input-group mb-3">
        <span className="input-group-text">name</span>
        <input type="text" className="form-control"
        value=''/>
       </div>

       
       <div className="input-group mb-3">
        <span className="input-group-text">count</span>
        <input type="text" className="form-control"
        value=''
        />
       </div>


   </div>

</div>
</div> 
        )
    }
}
