import React,{Component} from 'react';
import {variables} from '../api/api.js';
import Popup from '../components/controls/PopUp.js';
export class Tools extends Component{
    constructor(props){
        super(props);
        this.state={
            tools:[],
            users:[],
            modalTitle:"",
            name:"",
            id:0,
            count:0,
            summary:[]
        }
    }
    //коннект API
    refreshList(){
        fetch(variables.API_URL+'Summary')
        .then(response=>response.json())
        .then(data=>{
            this.setState({summary:data});
        });
    }

    componentDidMount(){
        this.refreshList();
        this.GetUsers();
        this.GetTools();
    }
    addClick(){
        this.setState({
            modalTitle:"Добавление записи",
            DepartmentId:0,
            name:"",
            count:0
        });
    }
    GetUsers(){
        fetch(variables.API_URL+'Users')
        .then(response=>response.json())
        .then(data=>{
            this.setState({users:data});
        });
    }
    GetTools(){
        fetch(variables.API_URL+'Tools')
        .then(response=>response.json())
        .then(data=>{
            this.setState({tools:data});
        });
    }

    deleteClick(id){
        fetch(`http://localhost:49146/api/Summary/${id}`,{
            method:'DELETE'
        }).then((result)=>{
            result.json().then((resp)=>{
                console.warn(resp)
            })
        })

    }

    // deleteClick(id){
    //     if(window.confirm('Are you sure?')){
    //     fetch(variables.API_URL+'Summary/'+ id,{
    //         method:'DELETE',
    //         headers:{
