import React,{Component} from 'react';
import {variables} from '../api/api.js';
import Popup from '../components/controls/PopUp.js';




export class Tools extends Component{


    constructor(props){
        super(props);

        this.state={
            tools:[],
            users:[],
            modalTitle:"",
            name:"",
            id:0,
            count:0,
        }
    }


    //коннект API
    refreshList(){
        fetch(variables.API_URL+'Tools')
        .then(response=>response.json())
        .then(data=>{
            this.setState({tools:data});
        });
    }

    componentDidMount(){
        this.refreshList();
        this.GetUsers();
        this.GetTools();
    }

    changename =(e)=>{
        this.setState({name:e.target.value});
    }

    addClick(){
        this.setState({
            modalTitle:"Add Tools",
            DepartmentId:0,
            name:"",
            count:0
        });
    }
    editClick(tools){
        this.setState({
            modalTitle:"Edit Tools",
            id:tools.DepartmentId,
            name:tools.name,
            count:tools.count
        });
    }

    createClick(){
        fetch(variables.API_URL+'Tools',{
            method:'POST',
            headers:{
                'Accept':'application/json',
                'Content-Type':'application/json'
            },
            body:JSON.stringify({
                id:this.state.id,
                name:this.state.name,
                count:this.state.count
            })
        })
        .then(res=>res.json())
        .then((result)=>{
            alert(result);
            this.refreshList();
        },(error)=>{
            alert('Failed');
        })
    }

    GetUsers(){
        fetch(variables.API_URL+'Users')
        .then(response=>response.json())
        .then(data=>{
            this.setState({users:data});
        });
    }

    GetTools(){
        fetch(variables.API_URL+'Tools')
        .then(response=>response.json())
        .then(data=>{
            this.setState({tools:data});
        });
    }




    deleteClick(id){
        if(window.confirm('Are you sure?')){
        fetch(variables.API_URL+'Users/'+id,{
            method:'DELETE',
            headers:{
                'Accept':'application/json',
                'Content-Type':'application/json'
            }
        })
        .then(res=>res.json())
        .then((result)=>{
            alert(result);
            this.refreshList();
        },(error)=>{
            alert('Failed');
        })
        }
    }

    render(){
        const {
            tools,
            modalTitle,
            Toolsid,
            name,
            count
        }=this.state;

        return(

<div>
    <button type="button"
    className="btn btn-primary m-2 float-end"
    data-bs-toggle="modal"
    data-bs-target="#exampleModal"
    onClick={()=>this.addClick()}>
        Add Department
    </button>
    <table className="table table-striped">
    <thead>
    <tr>
        <th>
            FIO      
        </th>
        <th>
            ToolsName
        </th>
        <th>
            Count
        </th>
        <th>
            Options
        </th>
    </tr>
    </thead>
    <tbody>
        {tools.map(tools=>
            <tr key={tools.id}>
                <td>{tools.FIO}</td>
                <td>{tools.name}</td>
                <td>{tools.countTools}</td>
                <td>
                <button type="button"
                className="btn btn-light mr-1"
                onClick={()=>this.deleteClick(tools.id)}>
                    <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-trash-fill" viewBox="0 0 16 16">
                    <path d="M2.5 1a1 1 0 0 0-1 1v1a1 1 0 0 0 1 1H3v9a2 2 0 0 0 2 2h6a2 2 0 0 0 2-2V4h.5a1 1 0 0 0 1-1V2a1 1 0 0 0-1-1H10a1 1 0 0 0-1-1H7a1 1 0 0 0-1 1H2.5zm3 4a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7a.5.5 0 0 1 .5-.5zM8 5a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7A.5.5 0 0 1 8 5zm3 .5v7a.5.5 0 0 1-1 0v-7a.5.5 0 0 1 1 0z"/>
                    </svg>
                </button>

                </td>
            </tr>
            )}
    </tbody>
    </table>




}
<Popup
user={this.state.users}
/>
</div>

        )
    }
}
