import React from 'react'
import Select from 'react-select';
import '../../App.css';
import {variables} from '../../api/api.js';
export default class PopUp extends React.Component{



    constructor(props){
        super(props);

    
    

        this.state={
            users:this.props.users,
            userValue:1,
            tools:this.props.tools,
            toolValue:0,
            options:[],
            count:0,
        }

        
    }
    setOptionUsers(data) {
        const s = data.map(user => (
            {value:user.id,label:user.FIO}
        ))
        return s;
    }
    setOptionTools(data) {
        const s = data.map(tool => (
            {value:tool.id,label:tool.name}
        ))
        return s;
    }

    componentDidUpdate() {

    }

    pushData = () => {
        fetch(variables.API_URL+'Update',{
            method:'POST',
            headers:{
                'Accept':'application/json',
                'Content-Type':'application/json'
            },
            body:JSON.stringify({
                user_id:this.state.userValue,
                tool_id:this.state.toolValue,
                count:this.state.count,
            })
        })
        .then(res=>res.json())
        .then((result)=>{
            alert(result);
            this.refreshList();
        },(error)=>{
            alert('Failed');
        })
        console.log({
                user_id:this.state.userValue,
                tool_id:this.state.toolValue,
                count:this.state.count,
        })
    }

    onClear = () => {
        console.log(this.userSelect);
    
    }



    
    render(){
    
        return(

             
            <div className="modal fade" id="exampleModal" tabIndex="-1" aria-hidden="true">
            <div className="modal-dialog modal-lg modal-dialog-centered">
            <div className="modal-content">
   <div className="modal-header">
       <h5 className="modal-title">Заголовок</h5>
       <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"
       ></button>
   </div>

   <div className="modal-body">
       <div className='container'>
       <Select ref={ref=>{this.userSelect = ref}}  className="ttt" style="margin-bottom:15px;"onChange={(e)=>{this.setState({userValue:e.value}); console.log(this.state.userValue);this.setOptionUsers(this.props.users)}} options={this.setOptionUsers(this.props.users)} />
       <Select ref={ref=>{this.toolSelect = ref}}  className="ttt" onChange={(e)=>{this.setState({toolValue:e.value}); console.log(this.state.toolValue);this.setOptionTools(this.props.tools)}} options={this.setOptionTools(this.props.tools)} />
       <input onChange={(e)=>{this.setState({count:e.target.value}); console.log(this.state.count)}} value={this.state.count}/>

       <this.Button onClick={this.pushData}>Create</this.Button>   
       <button onClick={this.onClear}>Clear</button>      
                <div className='col-md-4'></div>
            </div>
       </div>





   </div>

</div>
</div> 
        )
    }
}
